// Get current language from sessionStorage
function getCurrentLanguage() {
    return sessionStorage.getItem('selectedLanguage') || 'en';
}

// Set current language
function setCurrentLanguage(lang) {
    sessionStorage.setItem('selectedLanguage', lang);
    translatePage();
    
    // Re-translate dynamic content if on output page
    if (typeof retranslateConfirmationTests === 'function') {
        retranslateConfirmationTests(lang);
    }
    if (typeof retranslatePrecautions === 'function') {
        retranslatePrecautions(lang);
    }
}

// Translate a single text
function translateText(key, lang = null) {
    if (!lang) lang = getCurrentLanguage();
    return TRANSLATION_KEYS[lang]?.[key] || TRANSLATION_KEYS['en'][key] || key;
}

// Translate page elements
async function translatePage() {
    const lang = getCurrentLanguage();
    
    // Translate elements with data-translate attribute
    document.querySelectorAll('[data-translate]').forEach(element => {
        const key = element.getAttribute('data-translate');
        const translated = translateText(key, lang);
        if (element.tagName === 'INPUT' && element.type === 'submit') {
            element.value = translated;
        } else if (element.tagName === 'INPUT' || element.tagName === 'TEXTAREA') {
            element.placeholder = translated;
        } else {
            element.textContent = translated;
        }
    });
    
    // Translate dynamic content if needed
    if (typeof translateDynamicContent === 'function') {
        translateDynamicContent(lang);
    }
}

// Translate text using API (for dynamic content) - Now using static translations only
async function translateTextAPI(text, targetLang = null) {
    if (!targetLang) targetLang = getCurrentLanguage();
    
    // For now, return original text - translations are handled via static keys
    // In the future, you can add a translation dictionary here
    return text;
}

// Global variables to track speaking state
let isSpeaking = false;
let currentUtterance = null;
let isStopping = false; // Flag to track intentional stops

// Speak text using Browser TTS only - make it globally accessible
window.speakText = async function(text, langCode = null) {
    if (!langCode) langCode = getCurrentLanguage();
    
    // If already speaking, stop it
    if (isSpeaking) {
        stopSpeaking();
        return;
    }
    
    console.log('speakText called with:', text.substring(0, 50) + '...', 'lang:', langCode);
    
    // Use Browser TTS directly
    fallbackTTS(text, langCode);
};

// Stop speaking function
window.stopSpeaking = function() {
    if ('speechSynthesis' in window) {
        isStopping = true; // Set flag to indicate intentional stop
        speechSynthesis.cancel();
        isSpeaking = false;
        currentUtterance = null;
        updateSpeakButtons(false);
        console.log('Speech stopped');
        // Reset flag after a short delay to allow error handlers to check it
        setTimeout(() => {
            isStopping = false;
        }, 100);
    }
};

// Fallback to browser TTS
function fallbackTTS(text, langCode) {
    try {
        console.log('Using browser TTS fallback for language:', langCode);
        
        // Check if speech synthesis is available
        if (!('speechSynthesis' in window)) {
            alert('Speech synthesis is not supported in your browser. Please use a modern browser.');
            return;
        }
        
        // Stop any ongoing speech
        speechSynthesis.cancel();
        
        const utterance = new SpeechSynthesisUtterance(text);
        const langMap = {
            'en': 'en-US',
            'hi': 'hi-IN',
            'te': 'te-IN',
            'ta': 'ta-IN',
            'ml': 'ml-IN',
            'kn': 'kn-IN'
        };
        utterance.lang = langMap[langCode] || 'en-US';
        utterance.rate = 0.9;
        utterance.pitch = 1;
        utterance.volume = 1;
        
        // Store current utterance and set speaking state
        currentUtterance = utterance;
        isSpeaking = true;
        
        utterance.onstart = function() {
            console.log('Browser TTS started');
            isSpeaking = true;
            // Update button text to "Stop"
            updateSpeakButtons(true);
        };
        
        utterance.onend = function() {
            console.log('Browser TTS completed');
            isSpeaking = false;
            currentUtterance = null;
            // Update button text back to "Speak"
            updateSpeakButtons(false);
        };
        
        utterance.onerror = function(event) {
            console.error('Browser TTS error:', event);
            isSpeaking = false;
            currentUtterance = null;
            updateSpeakButtons(false);
            // Only show alert if it's not an intentional stop
            if (!isStopping) {
                alert('Unable to play audio. Please check your browser settings.');
            }
        };
        
        speechSynthesis.speak(utterance);
    } catch (error) {
        console.error('Browser TTS error:', error);
        isSpeaking = false;
        currentUtterance = null;
        updateSpeakButtons(false);
        // Only show alert if it's not an intentional stop
        if (!isStopping) {
            alert('Unable to play audio. Please check your browser settings.');
        }
    }
}

// Function to update speak button text
function updateSpeakButtons(speaking) {
    const lang = getCurrentLanguage();
    const speakButtons = document.querySelectorAll('.speak-btn');
    speakButtons.forEach(btn => {
        if (speaking) {
            // Change to Stop button
            const originalText = btn.textContent;
            if (!btn.getAttribute('data-original-text')) {
                btn.setAttribute('data-original-text', originalText);
            }
            // Extract just the text part (remove emoji if present)
            const stopText = translateText('stop', lang);
            btn.textContent = 'тП╕я╕П ' + stopText;
        } else {
            // Change back to Speak button
            const originalKey = btn.getAttribute('data-translate') || 'speak';
            const speakText = translateText(originalKey, lang);
            // If the translation already includes emoji, use it; otherwise add it
            if (speakText.includes('ЁЯФК')) {
                btn.textContent = speakText;
            } else {
                btn.textContent = 'ЁЯФК ' + speakText.replace('ЁЯФК ', '');
            }
            btn.removeAttribute('data-original-text');
        }
    });
}

// Initialize language selector
function initLanguageSelector() {
    const lang = getCurrentLanguage();
    const selectors = document.querySelectorAll('#language-selector');
    selectors.forEach(selector => {
        if (selector) {
            selector.value = lang;
            selector.addEventListener('change', function(e) {
                setCurrentLanguage(e.target.value);
            });
        }
    });
}

// Initialize on page load
document.addEventListener('DOMContentLoaded', function() {
    initLanguageSelector();
    translatePage();
});

